from fastapi import APIRouter, Depends, HTTPException
from typing import Optional
from datetime import datetime
from bson import ObjectId
from pydantic import BaseModel
import pyotp
import qrcode
import io
import base64
from app.models.user import UserInDB
from app.core.security import get_current_user
from app.db.mongodb import get_database

router = APIRouter()

class TwoFactorEnable(BaseModel):
    code: str

class TwoFactorVerify(BaseModel):
    code: str

@router.post("/enable")
async def enable_2fa(
    current_user: UserInDB = Depends(get_current_user)
):
    """Generate 2FA secret and QR code"""
    db = get_database()

    # Generate secret
    secret = pyotp.random_base32()

    # Create provisioning URI
    totp = pyotp.TOTP(secret)
    provisioning_uri = totp.provisioning_uri(
        name=current_user.email,
        issuer_name="Memory Hub"
    )

    # Generate QR code
    qr = qrcode.QRCode(version=1, box_size=10, border=5)
    qr.add_data(provisioning_uri)
    qr.make(fit=True)

    img = qr.make_image(fill_color="black", back_color="white")
    buffer = io.BytesIO()
    img.save(buffer, format='PNG')
    qr_code_base64 = base64.b64encode(buffer.getvalue()).decode()

    # Store secret temporarily
    await db.users.update_one(
        {"_id": ObjectId(current_user.id)},
        {"$set": {
            "two_factor_temp_secret": secret,
            "two_factor_enabled": False
        }}
    )

    return {
        "secret": secret,
        "qr_code": f"data:image/png;base64,{qr_code_base64}",
        "provisioning_uri": provisioning_uri
    }

@router.post("/verify-enable")
async def verify_and_enable_2fa(
    data: TwoFactorEnable,
    current_user: UserInDB = Depends(get_current_user)
):
    """Verify code and enable 2FA"""
    db = get_database()

    user_doc = await db.users.find_one({"_id": ObjectId(current_user.id)})
    temp_secret = user_doc.get("two_factor_temp_secret")

    if not temp_secret:
        raise HTTPException(status_code=400, detail="2FA setup not initiated")

    # Verify code
    totp = pyotp.TOTP(temp_secret)
    if not totp.verify(data.code):
        raise HTTPException(status_code=400, detail="Invalid code")

    # Enable 2FA
    await db.users.update_one(
        {"_id": ObjectId(current_user.id)},
        {
            "$set": {
                "two_factor_secret": temp_secret,
                "two_factor_enabled": True
            },
            "$unset": {"two_factor_temp_secret": ""}
        }
    )

    return {"message": "2FA enabled successfully"}

@router.post("/verify")
async def verify_2fa(
    data: TwoFactorVerify,
    current_user: UserInDB = Depends(get_current_user)
):
    """Verify 2FA code"""
    db = get_database()

    user_doc = await db.users.find_one({"_id": ObjectId(current_user.id)})

    if not user_doc.get("two_factor_enabled"):
        raise HTTPException(status_code=400, detail="2FA not enabled")

    secret = user_doc.get("two_factor_secret")
    totp = pyotp.TOTP(secret)

    if not totp.verify(data.code):
        raise HTTPException(status_code=400, detail="Invalid code")

    return {"message": "Code verified"}

@router.post("/disable")
async def disable_2fa(
    data: TwoFactorVerify,
    current_user: UserInDB = Depends(get_current_user)
):
    """Disable 2FA"""
    db = get_database()

    user_doc = await db.users.find_one({"_id": ObjectId(current_user.id)})

    if not user_doc.get("two_factor_enabled"):
        raise HTTPException(status_code=400, detail="2FA not enabled")

    # Verify code before disabling
    secret = user_doc.get("two_factor_secret")
    totp = pyotp.TOTP(secret)

    if not totp.verify(data.code):
        raise HTTPException(status_code=400, detail="Invalid code")

    # Disable 2FA
    await db.users.update_one(
        {"_id": ObjectId(current_user.id)},
        {
            "$set": {"two_factor_enabled": False},
            "$unset": {"two_factor_secret": ""}
        }
    )

    return {"message": "2FA disabled successfully"}

@router.get("/status")
async def get_2fa_status(
    current_user: UserInDB = Depends(get_current_user)
):
    """Check if 2FA is enabled"""
    db = get_database()

    user_doc = await db.users.find_one({"_id": ObjectId(current_user.id)})

    return {
        "enabled": user_doc.get("two_factor_enabled", False)
    }

app/api/v1/endpoints/reactions.py.content:
from fastapi import APIRouter, Depends, HTTPException
from typing import List
from datetime import datetime
from bson import ObjectId
from pydantic import BaseModel
from app.models.user import UserInDB
from app.core.security import get_current_user
from app.db.mongodb import get_database

router = APIRouter()

class ReactionCreate(BaseModel):
    target_type: str  # "memory", "comment", "story"
    target_id: str
    emoji: str

@router.post("/")
async def add_reaction(
    reaction: ReactionCreate,
    current_user: UserInDB = Depends(get_current_user)
):
    """Add a reaction to a memory, comment, or story"""
    db = get_database()

    # Check if reaction already exists
    existing = await db.reactions.find_one({
        "user_id": str(current_user.id),
        "target_type": reaction.target_type,
        "target_id": reaction.target_id
    })

    if existing:
        # Update existing reaction
        await db.reactions.update_one(
            {"_id": existing["_id"]},
            {"$set": {"emoji": reaction.emoji, "updated_at": datetime.utcnow()}}
        )
        reaction_data = existing
        reaction_data["emoji"] = reaction.emoji
    else:
        # Create new reaction
        reaction_data = {
            "user_id": str(current_user.id),
            "target_type": reaction.target_type,
            "target_id": reaction.target_id,
            "emoji": reaction.emoji,
            "created_at": datetime.utcnow()
        }
        result = await db.reactions.insert_one(reaction_data)
        reaction_data["_id"] = str(result.inserted_id)

    return reaction_data

@router.get("/{target_type}/{target_id}")
async def get_reactions(
    target_type: str,
    target_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Get all reactions for a target"""
    db = get_database()

    reactions = await db.reactions.find({
        "target_type": target_type,
        "target_id": target_id
    }).to_list(1000)

    # Group reactions by emoji
    reaction_summary = {}
    for reaction in reactions:
        emoji = reaction["emoji"]
        if emoji not in reaction_summary:
            reaction_summary[emoji] = {
                "emoji": emoji,
                "count": 0,
                "users": [],
                "reacted_by_current_user": False
            }
        reaction_summary[emoji]["count"] += 1
        reaction_summary[emoji]["users"].append(str(reaction["user_id"]))
        if str(reaction["user_id"]) == str(current_user.id):
            reaction_summary[emoji]["reacted_by_current_user"] = True

    return list(reaction_summary.values())

@router.delete("/{target_type}/{target_id}")
async def remove_reaction(
    target_type: str,
    target_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Remove user's reaction from a target"""
    db = get_database()

    result = await db.reactions.delete_one({
        "user_id": str(current_user.id),
        "target_type": target_type,
        "target_id": target_id
    })

    if result.deleted_count == 0:
        raise HTTPException(status_code=404, detail="Reaction not found")

    return {"message": "Reaction removed"}

@router.get("/user/stats")
async def get_user_reaction_stats(
    current_user: UserInDB = Depends(get_current_user)
):
    """Get statistics about user's reactions"""
    db = get_database()

    reactions = await db.reactions.find({
        "user_id": str(current_user.id)
    }).to_list(10000)

    emoji_counts = {}
    for reaction in reactions:
        emoji = reaction["emoji"]
        emoji_counts[emoji] = emoji_counts.get(emoji, 0) + 1

    return {
        "total_reactions": len(reactions),
        "emoji_breakdown": emoji_counts,
        "most_used_emoji": max(emoji_counts.items(), key=lambda x: x[1])[0] if emoji_counts else None
    }
