from datetime import datetime
from typing import List, Optional, Dict, Any
from fastapi import APIRouter, Depends, HTTPException, status, UploadFile, File
from fastapi.responses import FileResponse
from bson import ObjectId
import os
import shutil
from pathlib import Path

from app.core.security import get_current_user, oauth2_scheme
from app.core.hashing import get_password_hash
from app.db.mongodb import get_collection
from app.models.user import (
    UserInDB, UserCreate, UserUpdate, UserResponse, 
    UserProfileResponse, UserSettingsUpdate, UserRole
)

router = APIRouter()

# Configure upload directory
AVATAR_UPLOAD_DIR = "uploads/avatars"
os.makedirs(AVATAR_UPLOAD_DIR, exist_ok=True)

def safe_object_id(id_str: str) -> ObjectId:
    """Safely convert string to ObjectId, raise 400 if invalid"""
    try:
        return ObjectId(id_str)
    except Exception:
        raise HTTPException(status_code=400, detail="Invalid ID format")

def convert_user_doc(user_doc: dict) -> dict:
    """Convert MongoDB user document to response format with safe field handling"""
    if not user_doc:
        return None

    result = dict(user_doc)
    if "_id" in result:
        result["id"] = str(result.pop("_id"))

    # Ensure all required fields are present with defaults
    result.setdefault("email", "")
    result.setdefault("full_name", "")
    result.setdefault("avatar_url", None)
    result.setdefault("bio", None)
    result.setdefault("is_active", True)
    result.setdefault("role", UserRole.USER)
    result.setdefault("created_at", datetime.utcnow())
    result.setdefault("updated_at", datetime.utcnow())
    result.setdefault("city", None)
    result.setdefault("country", None)
    result.setdefault("website", None)

    return result

@router.get("/me", response_model=UserProfileResponse)
async def read_users_me(current_user: UserInDB = Depends(get_current_user)):
    """Get current user profile with stats"""
    try:
        # Get user stats with error handling
        stats = {
            "memories": await get_collection("memories").count_documents({"owner_id": ObjectId(current_user.id)}),
            "files": await get_collection("files").count_documents({"owner_id": ObjectId(current_user.id)}),
            "collections": await get_collection("collections").count_documents({"owner_id": ObjectId(current_user.id)}),
            "followers": await get_collection("relationships").count_documents({"following_id": ObjectId(current_user.id), "status": "accepted"}),
            "following": await get_collection("relationships").count_documents({"follower_id": ObjectId(current_user.id), "status": "accepted"})
        }

        user_dict = {
            "id": str(current_user.id),
            "email": current_user.email or "",
            "full_name": current_user.full_name or "",
            "avatar_url": current_user.avatar_url,
            "bio": current_user.bio,
            "city": getattr(current_user, "city", None),
            "country": getattr(current_user, "country", None),
            "website": getattr(current_user, "website", None),
            "is_active": current_user.is_active,
            "role": current_user.role,
            "created_at": current_user.created_at,
            "updated_at": current_user.updated_at,
            "stats": stats
        }
        return user_dict
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error fetching user profile: {str(e)}")

@router.put("/me", response_model=UserResponse)
async def update_user_me(
    user_update: UserUpdate,
    current_user: UserInDB = Depends(get_current_user)
):
    """Update current user profile"""
    try:
        update_data = user_update.dict(exclude_unset=True)
        update_data["updated_at"] = datetime.utcnow()

        if "email" in update_data and update_data["email"] != current_user.email:
            # Check if email is already taken
            existing_user = await get_collection("users").find_one({"email": update_data["email"]})
            if existing_user and str(existing_user["_id"]) != str(current_user.id):
                raise HTTPException(
                    status_code=status.HTTP_400_BAD_REQUEST,
                    detail="Email already registered"
                )

        await get_collection("users").update_one(
            {"_id": ObjectId(current_user.id)},
            {"$set": update_data}
        )

        updated_user = await get_collection("users").find_one({"_id": ObjectId(current_user.id)})
        return UserResponse(**convert_user_doc(updated_user))
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error updating profile: {str(e)}")

@router.put("/me/password")
async def change_password(
    current_password: str,
    new_password: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Change current user's password"""
    try:
        from app.core.security import verify_password

        # Verify current password
        if not verify_password(current_password, current_user.hashed_password):
            raise HTTPException(
                status_code=status.HTTP_400_BAD_REQUEST,
                detail="Incorrect current password"
            )

        # Update password
        hashed_password = get_password_hash(new_password)
        await get_collection("users").update_one(
            {"_id": ObjectId(current_user.id)},
            {"$set": {"hashed_password": hashed_password, "updated_at": datetime.utcnow()}}
        )

        return {"message": "Password updated successfully"}
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error changing password: {str(e)}")

@router.post("/me/avatar", response_model=UserResponse)
async def upload_avatar(
    file: UploadFile = File(...),
    current_user: UserInDB = Depends(get_current_user)
):
    """Upload user avatar"""
    try:
        # Validate file type
        allowed_types = ["image/jpeg", "image/png", "image/webp"]
        if file.content_type not in allowed_types:
            raise HTTPException(
                status_code=status.HTTP_400_BAD_REQUEST,
                detail="Only JPEG, PNG, and WebP images are allowed"
            )

        # Create user's avatar directory
        user_avatar_dir = os.path.join(AVATAR_UPLOAD_DIR, str(current_user.id))
        os.makedirs(user_avatar_dir, exist_ok=True)

        # Generate unique filename
        file_extension = Path(file.filename).suffix
        filename = f"avatar{file_extension}"
        file_path = os.path.join(user_avatar_dir, filename)

        # Save the file
        with open(file_path, "wb") as buffer:
            shutil.copyfileobj(file.file, buffer)

        # Update user's avatar URL
        avatar_url = f"/api/v1/users/me/avatar/{filename}"
        await get_collection("users").update_one(
            {"_id": ObjectId(current_user.id)},
            {"$set": {"avatar_url": avatar_url, "updated_at": datetime.utcnow()}}
        )

        # Return updated user
        updated_user = await get_collection("users").find_one({"_id": ObjectId(current_user.id)})
        return UserResponse(**convert_user_doc(updated_user))
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error uploading avatar: {str(e)}")

@router.get("/me/avatar/{filename}")
async def get_avatar(
    filename: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Get user avatar"""
    file_path = os.path.join(AVATAR_UPLOAD_DIR, str(current_user.id), filename)
    if not os.path.exists(file_path):
        raise HTTPException(status_code=404, detail="Avatar not found")

    return FileResponse(file_path)

@router.get("/settings", response_model=dict)
async def get_user_settings(current_user: UserInDB = Depends(get_current_user)):
    """Get current user settings"""
    try:
        user = await get_collection("users").find_one({"_id": ObjectId(current_user.id)})
        return user.get("settings", {
            "push_notifications": True,
            "email_notifications": True,
            "theme": "light",
            "language": "en",
            "privacy": {
                "profile_visible": True,
                "show_email": False,
                "allow_messages": True
            }
        })
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error fetching settings: {str(e)}")

@router.get("/{user_id}", response_model=UserProfileResponse)
async def get_user(
    user_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Get user profile by ID"""
    try:
        user_obj_id = safe_object_id(user_id)
        user = await get_collection("users").find_one({"_id": user_obj_id})

        if not user:
            raise HTTPException(status_code=404, detail="User not found")

        # Get user stats
        stats = {
            "memories": await get_collection("memories").count_documents({
                "owner_id": user_obj_id,
                "$or": [
                    {"privacy": "public"},
                    {"owner_id": ObjectId(current_user.id)}
                ]
            }),
            "files": await get_collection("files").count_documents({
                "owner_id": user_obj_id,
                "privacy": "public"
            }),
            "collections": await get_collection("collections").count_documents({
                "owner_id": user_obj_id,
                "$or": [
                    {"privacy": "public"},
                    {"owner_id": ObjectId(current_user.id)}
                ]
            }),
            "followers": await get_collection("relationships").count_documents({
                "following_id": user_obj_id,
                "status": "accepted"
            }),
            "following": await get_collection("relationships").count_documents({
                "follower_id": user_obj_id,
                "status": "accepted"
            })
        }

        user_dict = convert_user_doc(user)
        user_dict["stats"] = stats
        return user_dict
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error fetching user: {str(e)}")

@router.get("/", response_model=List[UserResponse])
async def list_users(
    search: Optional[str] = None,
    page: int = 1,
    limit: int = 20,
    current_user: UserInDB = Depends(get_current_user)
):
    """List users with search and pagination"""
    try:
        query = {}
        if search:
            query["$or"] = [
                {"email": {"$regex": search, "$options": "i"}},
                {"full_name": {"$regex": search, "$options": "i"}}
            ]

        skip = (page - 1) * limit
        cursor = get_collection("users").find(query).skip(skip).limit(limit)

        users = []
        async for user in cursor:
            user_data = convert_user_doc(user)
            users.append(user_data)

        return users
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error listing users: {str(e)}")

@router.put("/me/settings", response_model=UserResponse)
async def update_user_settings(
    settings_update: UserSettingsUpdate,
    current_user: UserInDB = Depends(get_current_user)
):
    """Update current user settings"""
    try:
        update_data = settings_update.dict(exclude_unset=True)

        await get_collection("users").update_one(
            {"_id": ObjectId(current_user.id)},
            {"$set": {"settings": update_data, "updated_at": datetime.utcnow()}}
        )

        updated_user = await get_collection("users").find_one({"_id": ObjectId(current_user.id)})
        return UserResponse(**convert_user_doc(updated_user))
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error updating settings: {str(e)}")

@router.delete("/me", status_code=status.HTTP_204_NO_CONTENT)
async def delete_user_me(
    current_user: UserInDB = Depends(get_current_user)
):
    """Delete current user account (GDPR right to deletion)"""
    try:
        # Soft delete - mark as inactive and anonymize
        await get_collection("users").update_one(
            {"_id": ObjectId(current_user.id)},
            {"$set": {
                "is_active": False,
                "email": f"deleted_{current_user.id}@deleted.local",
                "full_name": "Deleted User",
                "bio": None,
                "avatar_url": None,
                "deleted_at": datetime.utcnow()
            }}
        )

        # Anonymize user's data
        await get_collection("memories").update_many(
            {"owner_id": ObjectId(current_user.id)},
            {"$set": {"privacy": "private"}}
        )

        return None
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error deleting account: {str(e)}")

@router.get("/{user_id}/profile")
async def get_user_profile(
    user_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Get a user's full profile with recent memories and stats"""
    try:
        user_obj_id = safe_object_id(user_id)
        user_doc = await get_collection("users").find_one({"_id": user_obj_id})

        if not user_doc:
            raise HTTPException(status_code=404, detail="User not found")

        # Check if user is deleted/inactive
        if not user_doc.get("is_active", True):
            raise HTTPException(status_code=404, detail="User not found")

        # Get relationship status
        relationship = await get_collection("relationships").find_one({
            "follower_id": ObjectId(current_user.id),
            "following_id": user_obj_id
        })

        # Get user stats
        stats = {
            "memories": await get_collection("memories").count_documents({"owner_id": user_obj_id}),
            "files": await get_collection("files").count_documents({"owner_id": user_obj_id}),
            "collections": await get_collection("collections").count_documents({"owner_id": user_obj_id}),
            "followers": await get_collection("relationships").count_documents({"following_id": user_obj_id, "status": "accepted"}),
            "following": await get_collection("relationships").count_documents({"follower_id": user_obj_id, "status": "accepted"})
        }

        # Get recent public/friends memories (based on privacy and relationship)
        memory_query = {"owner_id": user_obj_id}
        if str(user_id) != str(current_user.id):
            if relationship and relationship.get("status") == "accepted":
                memory_query["privacy"] = {"$in": ["public", "friends"]}
            else:
                memory_query["privacy"] = "public"

        cursor = get_collection("memories").find(memory_query).sort("created_at", -1).limit(10)

        recent_memories = []
        async for memory_doc in cursor:
            recent_memories.append({
                "id": str(memory_doc["_id"]),
                "title": memory_doc.get("title", "Untitled"),
                "content": memory_doc.get("content", "")[:200],
                "media_urls": memory_doc.get("media_urls", []),
                "tags": memory_doc.get("tags", []),
                "created_at": memory_doc.get("created_at", datetime.utcnow()),
                "like_count": memory_doc.get("like_count", 0)
            })

        return {
            "id": str(user_doc["_id"]),
            "email": user_doc.get("email", ""),
            "full_name": user_doc.get("full_name", ""),
            "avatar_url": user_doc.get("avatar_url"),
            "bio": user_doc.get("bio"),
            "city": user_doc.get("city"),
            "country": user_doc.get("country"),
            "website": user_doc.get("website"),
            "created_at": user_doc.get("created_at", datetime.utcnow()),
            "stats": stats,
            "recent_memories": recent_memories,
            "is_following": relationship is not None and relationship.get("status") == "accepted",
            "is_own_profile": str(user_id) == str(current_user.id)
        }
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error fetching profile: {str(e)}")

app/api/v1/endpoints/memories.py.content:
import os
import json
from datetime import datetime
from typing import List, Optional
from fastapi import (
    APIRouter, Depends, HTTPException, status, 
    UploadFile, File, Form, Query
)
from fastapi.responses import FileResponse
from bson import ObjectId
import shutil
import uuid

from app.core.security import get_current_user
from app.db.mongodb import get_collection
from app.models.memory import (
    MemoryCreate, MemoryInDB, MemoryUpdate, 
    MemoryResponse, MemorySearchParams, MemoryPrivacy
)
from app.models.user import UserInDB
from app.utils.memory_utils import (
    process_memory_search_filters, 
    get_sort_params,
    increment_memory_counter
)
from app.core.config import settings

router = APIRouter()

# Configure upload directory
UPLOAD_DIR = "uploads/memories"
os.makedirs(UPLOAD_DIR, exist_ok=True)

@router.post("/", response_model=MemoryInDB)
async def create_memory(
    title: str = Form(...),
    content: str = Form(...),
    tags: str = Form("[]"),  # Accept tags as JSON string
    privacy: MemoryPrivacy = Form(MemoryPrivacy.PRIVATE),
    location: Optional[str] = Form(None),
    mood: Optional[str] = Form(None),
    tagged_family_members: str = Form("[]"),  # JSON array of {"user_id": "xxx", "relation": "mom"}
    family_circle_ids: str = Form("[]"),  # JSON array of circle IDs
    relationship_context: Optional[str] = Form(None),  # e.g., "Mom's Birthday"
    files: List[UploadFile] = File([]),
    current_user: UserInDB = Depends(get_current_user)
):
    # Parse tags from JSON string
    try:
        tags_list = json.loads(tags) if tags else []
    except json.JSONDecodeError:
        tags_list = []

    # Parse family tags
    try:
        tagged_family = json.loads(tagged_family_members) if tagged_family_members else []
    except json.JSONDecodeError:
        tagged_family = []

    # Parse family circles
    try:
        family_circles = json.loads(family_circle_ids) if family_circle_ids else []
    except json.JSONDecodeError:
        family_circles = []

    # Validate tagged family members - ensure they are in user's family relationships
    validated_family_tags = []
    for family_member in tagged_family:
        if not family_member.get("user_id"):
            continue

        try:
            member_oid = ObjectId(family_member["user_id"])
        except:
            continue

        # Verify this is actually a family relationship of the current user
        relationship = await get_collection("family_relationships").find_one({
            "user_id": ObjectId(current_user.id),
            "related_user_id": member_oid
        })

        if relationship:
            validated_family_tags.append(family_member)

    # Validate family circles - ensure user is a member
    validated_circles = []
    for circle_id in family_circles:
        try:
            circle_oid = ObjectId(circle_id)
        except:
            continue

        # Verify user is a member of this circle
        circle = await get_collection("family_circles").find_one({
            "_id": circle_oid,
            "member_ids": ObjectId(current_user.id)
        })

        if circle:
            validated_circles.append(circle_id)

    # Save uploaded files
    media_urls = []
    for file in files:
        if file.filename:
            file_extension = os.path.splitext(file.filename)[1]
            unique_filename = f"{uuid.uuid4()}{file_extension}"
            file_path = os.path.join(UPLOAD_DIR, unique_filename)

            with open(file_path, "wb") as buffer:
                shutil.copyfileobj(file.file, buffer)

            media_url = f"/api/v1/memories/media/{unique_filename}"
            media_urls.append(media_url)

    # Create memory
    memory_data = {
        "title": title,
        "content": content,
        "tags": tags_list,
        "privacy": privacy,
        "media_urls": media_urls,
        "owner_id": ObjectId(current_user.id),
        "mood": mood,
        "tagged_family_members": validated_family_tags,
        "family_circle_ids": validated_circles,
        "relationship_context": relationship_context,
        "created_at": datetime.utcnow(),
        "updated_at": datetime.utcnow(),
        "view_count": 0,
        "like_count": 0,
        "comment_count": 0
    }

    if location:
        try:
            lat, lng = map(float, location.split(','))
            memory_data["location"] = {"lat": lat, "lng": lng}
        except:
            pass

    result = await get_collection("memories").insert_one(memory_data)
    memory = await get_collection("memories").find_one({"_id": result.inserted_id})

    # Send notifications to tagged family members (using validated list)
    for family_member in validated_family_tags:
        if family_member.get("user_id"):
            try:
                await get_collection("notifications").insert_one({
                    "user_id": ObjectId(family_member["user_id"]),
                    "type": "family_tag",
                    "title": f"{current_user.full_name} tagged you in a memory",
                    "message": f"You were tagged as '{family_member.get('relation', 'family')}' in '{title}'",
                    "link": f"/memories/{str(result.inserted_id)}",
                    "read": False,
                    "created_at": datetime.utcnow()
                })
            except:
                pass  # Silent fail for notifications

    return await _prepare_memory_response(memory, current_user.id)

@router.get("/media/{filename}")
async def get_media(filename: str):
    file_path = os.path.join(UPLOAD_DIR, filename)
    if not os.path.exists(file_path):
        raise HTTPException(status_code=404, detail="File not found")
    return FileResponse(file_path)

@router.get("/search/", response_model=List[MemoryResponse])
async def search_memories(
    query: Optional[str] = None,
    tags: Optional[List[str]] = Query(None),
    privacy: Optional[MemoryPrivacy] = None,
    start_date: Optional[datetime] = None,
    end_date: Optional[datetime] = None,
    sort_by: str = "created_at",
    sort_order: str = "desc",
    page: int = 1,
    limit: int = 20,
    current_user: UserInDB = Depends(get_current_user)
):
    search_params = {
        "query": query,
        "tags": tags,
        "privacy": privacy,
        "start_date": start_date,
        "end_date": end_date,
        "sort_by": sort_by,
        "sort_order": sort_order,
        "page": page,
        "limit": limit
    }

    filters = await process_memory_search_filters(search_params, current_user.id)
    sort = get_sort_params(sort_by, sort_order)

    skip = (page - 1) * limit
    cursor = get_collection("memories").find(filters).sort(sort).skip(skip).limit(limit)

    memories = []
    async for memory in cursor:
        memories.append(await _prepare_memory_response(memory, current_user.id))

    return memories

@router.get("/{memory_id}", response_model=MemoryResponse)
async def get_memory(
    memory_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    memory = await get_collection("memories").find_one({"_id": ObjectId(memory_id)})
    if not memory:
        raise HTTPException(status_code=404, detail="Memory not found")

    # Increment view count
    await increment_memory_counter(memory_id, "view_count")

    return await _prepare_memory_response(memory, current_user.id)

async def _prepare_memory_response(memory: dict, current_user_id: str) -> dict:
    memory["id"] = str(memory["_id"])
    memory["owner_id"] = str(memory["owner_id"])

    # Add additional user data
    user = await get_collection("users").find_one({"_id": ObjectId(memory["owner_id"])})
    if user:
        memory["owner_name"] = user.get("full_name")
        memory["owner_avatar"] = user.get("avatar_url")

    # Check if current user has liked or bookmarked this memory
    memory["is_liked"] = await get_collection("likes").find_one({
        "memory_id": ObjectId(memory["_id"]),
        "user_id": ObjectId(current_user_id)
    }) is not None

    memory["is_bookmarked"] = await get_collection("bookmarks").find_one({
        "memory_id": ObjectId(memory["_id"]),
        "user_id": ObjectId(current_user_id)
    }) is not None

    return memory

# Add more endpoints for likes, comments, bookmarks, etc.
@router.post("/{memory_id}/like")
async def like_memory(
    memory_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    # Check if already liked
    existing_like = await get_collection("likes").find_one({
        "memory_id": ObjectId(memory_id),
        "user_id": ObjectId(current_user.id)
    })

    if existing_like:
        # Unlike
        await get_collection("likes").delete_one({"_id": existing_like["_id"]})
        await increment_memory_counter(memory_id, "like_count", -1)
        return {"liked": False}
    else:
        # Like
        await get_collection("likes").insert_one({
            "memory_id": ObjectId(memory_id),
            "user_id": ObjectId(current_user.id),
            "created_at": datetime.utcnow()
        })
        await increment_memory_counter(memory_id, "like_count", 1)
        return {"liked": True}

@router.post("/{memory_id}/bookmark")
async def bookmark_memory(
    memory_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    # Check if already bookmarked
    existing_bookmark = await get_collection("bookmarks").find_one({
        "memory_id": ObjectId(memory_id),
        "user_id": ObjectId(current_user.id)
    })

    if existing_bookmark:
        # Remove bookmark
        await get_collection("bookmarks").delete_one({"_id": existing_bookmark["_id"]})
        return {"bookmarked": False}
    else:
        # Add bookmark
        await get_collection("bookmarks").insert_one({
            "memory_id": ObjectId(memory_id),
            "user_id": ObjectId(current_user.id),
            "created_at": datetime.utcnow()
        })
        return {"bookmarked": True}

# Add more endpoints as needed...

