from fastapi import APIRouter, Depends, Query
from typing import List, Optional, Dict, Any
from bson import ObjectId

from app.models.user import UserInDB
from app.core.security import get_current_user
from app.db.mongodb import get_collection

router = APIRouter()

@router.get("/")
async def advanced_search(
    q: str = Query(..., min_length=1),
    content_type: Optional[str] = None,
    tags: Optional[List[str]] = Query(None),
    start_date: Optional[str] = None,
    end_date: Optional[str] = None,
    page: int = Query(1, ge=1),
    limit: int = Query(20, ge=1, le=100),
    current_user: UserInDB = Depends(get_current_user)
):
    """Advanced search across all content types"""
    results = []

    # Search memories
    if not content_type or content_type == "memory":
        memory_query = {
            "owner_id": ObjectId(current_user.id),
            "$or": [
                {"title": {"$regex": q, "$options": "i"}},
                {"content": {"$regex": q, "$options": "i"}}
            ]
        }
        if tags:
            memory_query["tags"] = {"$in": tags}

        memories = await get_collection("memories").find(memory_query).limit(limit).to_list(length=None)
        for memory in memories:
            results.append({
                "type": "memory",
                "id": str(memory["_id"]),
                "title": memory["title"],
                "content": memory.get("content", "")[:200],
                "tags": memory.get("tags", []),
                "created_at": memory.get("created_at")
            })

    # Search files
    if not content_type or content_type == "file":
        file_query = {
            "owner_id": ObjectId(current_user.id),
            "$or": [
                {"name": {"$regex": q, "$options": "i"}},
                {"description": {"$regex": q, "$options": "i"}}
            ]
        }
        if tags:
            file_query["tags"] = {"$in": tags}

        files = await get_collection("files").find(file_query).limit(limit).to_list(length=None)
        for file in files:
            results.append({
                "type": "file",
                "id": str(file["_id"]),
                "name": file["name"],
                "description": file.get("description", ""),
                "tags": file.get("tags", []),
                "created_at": file.get("created_at")
            })

    # Search hub items
    if not content_type or content_type == "hub_item":
        hub_query = {
            "owner_id": ObjectId(current_user.id),
            "$or": [
                {"title": {"$regex": q, "$options": "i"}},
                {"content": {"$regex": q, "$options": "i"}}
            ]
        }
        if tags:
            hub_query["tags"] = {"$in": tags}

        hub_items = await get_collection("hub_items").find(hub_query).limit(limit).to_list(length=None)
        for item in hub_items:
            results.append({
                "type": "hub_item",
                "id": str(item["_id"]),
                "title": item["title"],
                "content": item.get("content", "")[:200],
                "tags": item.get("tags", []),
                "created_at": item.get("created_at")
            })

    # Search collections
    if not content_type or content_type == "collection":
        col_query = {
            "owner_id": ObjectId(current_user.id),
            "$or": [
                {"name": {"$regex": q, "$options": "i"}},
                {"description": {"$regex": q, "$options": "i"}}
            ]
        }
        if tags:
            col_query["tags"] = {"$in": tags}

        collections = await get_collection("collections").find(col_query).limit(limit).to_list(length=None)
        for col in collections:
            results.append({
                "type": "collection",
                "id": str(col["_id"]),
                "name": col["name"],
                "description": col.get("description", ""),
                "tags": col.get("tags", []),
                "created_at": col.get("created_at")
            })

    # Paginate
    skip = (page - 1) * limit
    paginated_results = results[skip:skip + limit]

    return {
        "results": paginated_results,
        "total": len(results),
        "page": page,
        "pages": (len(results) + limit - 1) // limit
    }

@router.get("/suggestions")
async def search_suggestions(
    q: str = Query(..., min_length=1),
    current_user: UserInDB = Depends(get_current_user)
):
    """Get search suggestions based on query"""
    suggestions = []

    # Get tag suggestions
    tags_cursor = get_collection("memories").aggregate([
        {"$match": {"owner_id": ObjectId(current_user.id)}},
        {"$unwind": "$tags"},
        {"$match": {"tags": {"$regex": q, "$options": "i"}}},
        {"$group": {"_id": "$tags", "count": {"$sum": 1}}},
        {"$sort": {"count": -1}},
        {"$limit": 5}
    ])

    async for tag in tags_cursor:
        suggestions.append({
            "type": "tag",
            "value": tag["_id"],
            "count": tag["count"]
        })

    # Get title suggestions from memories
    memories = await get_collection("memories").find({
        "owner_id": ObjectId(current_user.id),
        "title": {"$regex": q, "$options": "i"}
    }).limit(5).to_list(length=None)

    for memory in memories:
        suggestions.append({
            "type": "memory",
            "value": memory["title"],
            "id": str(memory["_id"])
        })

    return {"suggestions": suggestions}

app/api/v1/endpoints/analytics.py.content:
from fastapi import APIRouter, Depends, Query
from typing import Dict, Any, List
from datetime import datetime, timedelta
from bson import ObjectId

from app.models.user import UserInDB
from app.core.security import get_current_user
from app.db.mongodb import get_collection

router = APIRouter()

@router.get("/overview")
async def get_analytics_overview(
    current_user: UserInDB = Depends(get_current_user)
):
    """Get analytics overview with key metrics"""
    # Count all content types
    memories_count = await get_collection("memories").count_documents({"owner_id": ObjectId(current_user.id)})
    files_count = await get_collection("files").count_documents({"owner_id": ObjectId(current_user.id)})
    hub_items_count = await get_collection("hub_items").count_documents({"owner_id": ObjectId(current_user.id)})
    collections_count = await get_collection("collections").count_documents({"owner_id": ObjectId(current_user.id)})

    # Count social metrics
    followers_count = await get_collection("relationships").count_documents({
        "following_id": ObjectId(current_user.id),
        "status": "accepted"
    })
    following_count = await get_collection("relationships").count_documents({
        "follower_id": ObjectId(current_user.id),
        "status": "accepted"
    })

    # Get total storage used
    storage_pipeline = [
        {"$match": {"owner_id": ObjectId(current_user.id)}},
        {"$group": {"_id": None, "total_size": {"$sum": "$file_size"}}}
    ]
    storage_result = await get_collection("files").aggregate(storage_pipeline).to_list(length=1)
    total_storage = storage_result[0]["total_size"] if storage_result else 0

    return {
        "content": {
            "memories": memories_count,
            "files": files_count,
            "hub_items": hub_items_count,
            "collections": collections_count
        },
        "social": {
            "followers": followers_count,
            "following": following_count
        },
        "storage": {
            "used_bytes": total_storage,
            "used_mb": round(total_storage / (1024 * 1024), 2)
        }
    }

@router.get("/activity-chart")
async def get_activity_chart(
    period: str = Query("30d", regex="^(7d|30d|90d|1y)$"),
    current_user: UserInDB = Depends(get_current_user)
):
    """Get activity chart data for a period"""
    days_map = {"7d": 7, "30d": 30, "90d": 90, "1y": 365}
    days = days_map[period]

    start_date = datetime.utcnow() - timedelta(days=days)

    # Get memories created per day
    memories_pipeline = [
        {"$match": {
            "owner_id": ObjectId(current_user.id),
            "created_at": {"$gte": start_date}
        }},
        {"$group": {
            "_id": {"$dateToString": {"format": "%Y-%m-%d", "date": "$created_at"}},
            "count": {"$sum": 1}
        }},
        {"$sort": {"_id": 1}}
    ]

    memories_data = await get_collection("memories").aggregate(memories_pipeline).to_list(length=None)

    # Get files uploaded per day
    files_pipeline = [
        {"$match": {
            "owner_id": ObjectId(current_user.id),
            "created_at": {"$gte": start_date}
        }},
        {"$group": {
            "_id": {"$dateToString": {"format": "%Y-%m-%d", "date": "$created_at"}},
            "count": {"$sum": 1}
        }},
        {"$sort": {"_id": 1}}
    ]

    files_data = await get_collection("files").aggregate(files_pipeline).to_list(length=None)

    return {
        "period": period,
        "memories": [{"date": item["_id"], "count": item["count"]} for item in memories_data],
        "files": [{"date": item["_id"], "count": item["count"]} for item in files_data]
    }

@router.get("/top-tags")
async def get_top_tags(
    limit: int = Query(10, ge=1, le=50),
    current_user: UserInDB = Depends(get_current_user)
):
    """Get most used tags"""
    pipeline = [
        {"$match": {"owner_id": ObjectId(current_user.id)}},
        {"$unwind": "$tags"},
        {"$group": {"_id": "$tags", "count": {"$sum": 1}}},
        {"$sort": {"count": -1}},
        {"$limit": limit}
    ]

    tags_data = await get_collection("memories").aggregate(pipeline).to_list(length=None)

    return {
        "tags": [{"tag": item["_id"], "count": item["count"]} for item in tags_data]
    }

@router.get("/mood-trends")
async def get_mood_trends(
    period: str = Query("30d", regex="^(7d|30d|90d)$"),
    current_user: UserInDB = Depends(get_current_user)
):
    """Get mood trends over time"""
    days_map = {"7d": 7, "30d": 30, "90d": 90}
    days = days_map[period]

    start_date = datetime.utcnow() - timedelta(days=days)

    pipeline = [
        {"$match": {
            "owner_id": ObjectId(current_user.id),
            "mood": {"$exists": True, "$ne": None},
            "created_at": {"$gte": start_date}
        }},
        {"$group": {
            "_id": "$mood",
            "count": {"$sum": 1}
        }},
        {"$sort": {"count": -1}}
    ]

    mood_data = await get_collection("memories").aggregate(pipeline).to_list(length=None)

    return {
        "period": period,
        "moods": [{"mood": item["_id"], "count": item["count"]} for item in mood_data]
    }

@router.get("/storage-breakdown")
async def get_storage_breakdown(
    current_user: UserInDB = Depends(get_current_user)
):
    """Get storage breakdown by file type"""
    pipeline = [
        {"$match": {"owner_id": ObjectId(current_user.id)}},
        {"$group": {
            "_id": "$file_type",
            "total_size": {"$sum": "$file_size"},
            "count": {"$sum": 1}
        }},
        {"$sort": {"total_size": -1}}
    ]

    storage_data = await get_collection("files").aggregate(pipeline).to_list(length=None)

    return {
        "breakdown": [
            {
                "file_type": item["_id"] or "unknown",
                "total_size_bytes": item["total_size"],
                "total_size_mb": round(item["total_size"] / (1024 * 1024), 2),
                "count": item["count"]
            }
            for item in storage_data
        ]
    }

app/api/v1/endpoints/admin.py.content:
from fastapi import APIRouter, Depends, HTTPException, status, Query
from typing import List, Dict, Any
from datetime import datetime, timedelta
from bson import ObjectId

from app.models.user import UserInDB
from app.core.security import get_current_user
from app.db.mongodb import get_collection

router = APIRouter()

async def verify_admin(current_user: UserInDB = Depends(get_current_user)):
    """Verify user is admin"""
    if current_user.role != "admin":
        raise HTTPException(status_code=403, detail="Admin access required")
    return current_user

@router.get("/stats/overview")
async def get_admin_overview(
    admin: UserInDB = Depends(verify_admin)
):
    """Get admin dashboard overview statistics"""
    total_users = await get_collection("users").count_documents({})
    total_memories = await get_collection("memories").count_documents({})
    total_files = await get_collection("files").count_documents({})
    total_collections = await get_collection("collections").count_documents({})
    total_hubs = await get_collection("hubs").count_documents({})

    # Active users (logged in last 24 hours - approximation based on recent activity)
    yesterday = datetime.utcnow() - timedelta(days=1)
    active_users_count = await get_collection("memories").distinct("owner_id", {
        "created_at": {"$gte": yesterday}
    })

    # User growth (new users last 7 days)
    week_ago = datetime.utcnow() - timedelta(days=7)
    new_users = await get_collection("users").count_documents({
        "created_at": {"$gte": week_ago}
    })

    # Storage stats
    storage_pipeline = [
        {"$group": {"_id": None, "total_size": {"$sum": "$file_size"}}}
    ]
    storage_result = await get_collection("files").aggregate(storage_pipeline).to_list(length=1)
    total_storage = storage_result[0]["total_size"] if storage_result else 0

    return {
        "users": {
            "total": total_users,
            "active_24h": len(active_users_count),
            "new_7d": new_users
        },
        "content": {
            "memories": total_memories,
            "files": total_files,
            "collections": total_collections,
            "hubs": total_hubs
        },
        "storage": {
            "total_bytes": total_storage,
            "total_gb": round(total_storage / (1024 ** 3), 2)
        }
    }

@router.get("/users")
async def list_all_users(
    page: int = Query(1, ge=1),
    limit: int = Query(20, ge=1, le=100),
    search: str = Query(None),
    admin: UserInDB = Depends(verify_admin)
):
    """List all users with pagination and search"""
    query = {}
    if search:
        query["$or"] = [
            {"email": {"$regex": search, "$options": "i"}},
            {"full_name": {"$regex": search, "$options": "i"}}
        ]

    total = await get_collection("users").count_documents(query)
    skip = (page - 1) * limit

    cursor = get_collection("users").find(query).sort("created_at", -1).skip(skip).limit(limit)

    users = []
    async for user_doc in cursor:
        # Get user stats
        memories_count = await get_collection("memories").count_documents({"owner_id": user_doc["_id"]})
        files_count = await get_collection("files").count_documents({"owner_id": user_doc["_id"]})

        users.append({
            "id": str(user_doc["_id"]),
            "email": user_doc["email"],
            "full_name": user_doc.get("full_name"),
            "role": user_doc.get("role", "user"),
            "is_active": user_doc.get("is_active", True),
            "created_at": user_doc.get("created_at"),
            "stats": {
                "memories": memories_count,
                "files": files_count
            }
        })

    return {
        "users": users,
        "total": total,
        "page": page,
        "pages": (total + limit - 1) // limit
    }

@router.put("/users/{user_id}/role")
async def update_user_role(
    user_id: str,
    role: str = Query(..., regex="^(user|admin)$"),
    admin: UserInDB = Depends(verify_admin)
):
    """Update user role"""
    result = await get_collection("users").update_one(
        {"_id": ObjectId(user_id)},
        {"$set": {"role": role}}
    )

    if result.matched_count == 0:
        raise HTTPException(status_code=404, detail="User not found")

    return {"message": f"User role updated to {role}"}

@router.put("/users/{user_id}/status")
async def update_user_status(
    user_id: str,
    is_active: bool,
    admin: UserInDB = Depends(verify_admin)
):
    """Activate or deactivate user"""
    result = await get_collection("users").update_one(
        {"_id": ObjectId(user_id)},
        {"$set": {"is_active": is_active}}
    )

    if result.matched_count == 0:
        raise HTTPException(status_code=404, detail="User not found")

    status_text = "activated" if is_active else "deactivated"
    return {"message": f"User {status_text}"}

@router.delete("/users/{user_id}")
async def delete_user(
    user_id: str,
    admin: UserInDB = Depends(verify_admin)
):
    """Delete user and all their data"""
    user_object_id = ObjectId(user_id)

    # Delete user data
    await get_collection("memories").delete_many({"owner_id": user_object_id})
    await get_collection("files").delete_many({"owner_id": user_object_id})
    await get_collection("hub_items").delete_many({"owner_id": user_object_id})
    await get_collection("collections").delete_many({"owner_id": user_object_id})
    await get_collection("notifications").delete_many({"user_id": user_object_id})
    await get_collection("reminders").delete_many({"user_id": user_object_id})
    await get_collection("relationships").delete_many({
        "$or": [
            {"follower_id": user_object_id},
            {"following_id": user_object_id}
        ]
    })

    # Delete user
    result = await get_collection("users").delete_one({"_id": user_object_id})

    if result.deleted_count == 0:
        raise HTTPException(status_code=404, detail="User not found")

    return {"message": "User and all data deleted"}

@router.get("/stats/activity")
async def get_activity_stats(
    period: str = Query("7d", regex="^(7d|30d|90d)$"),
    admin: UserInDB = Depends(verify_admin)
):
    """Get platform activity statistics"""
    days_map = {"7d": 7, "30d": 30, "90d": 90}
    days = days_map[period]
    start_date = datetime.utcnow() - timedelta(days=days)

    # User registrations over time
    users_pipeline = [
        {"$match": {"created_at": {"$gte": start_date}}},
        {"$group": {
            "_id": {"$dateToString": {"format": "%Y-%m-%d", "date": "$created_at"}},
            "count": {"$sum": 1}
        }},
        {"$sort": {"_id": 1}}
    ]

    user_growth = await get_collection("users").aggregate(users_pipeline).to_list(length=None)

    # Content creation over time
    memories_pipeline = [
        {"$match": {"created_at": {"$gte": start_date}}},
        {"$group": {
            "_id": {"$dateToString": {"format": "%Y-%m-%d", "date": "$created_at"}},
            "count": {"$sum": 1}
        }},
        {"$sort": {"_id": 1}}
    ]

    content_creation = await get_collection("memories").aggregate(memories_pipeline).to_list(length=None)

    return {
        "period": period,
        "user_growth": [{"date": item["_id"], "count": item["count"]} for item in user_growth],
        "content_creation": [{"date": item["_id"], "count": item["count"]} for item in content_creation]
    }

@router.get("/stats/popular-tags")
async def get_popular_tags(
    limit: int = Query(20, ge=1, le=100),
    admin: UserInDB = Depends(verify_admin)
):
    """Get most popular tags across platform"""
    pipeline = [
        {"$unwind": "$tags"},
        {"$group": {"_id": "$tags", "count": {"$sum": 1}}},
        {"$sort": {"count": -1}},
        {"$limit": limit}
    ]

    tags_data = await get_collection("memories").aggregate(pipeline).to_list(length=None)

    return {
        "tags": [{"tag": item["_id"], "count": item["count"]} for item in tags_data]
    }

app/api/v1/endpoints/collections.py.content:
from fastapi import APIRouter, Depends, HTTPException, status, Query
from typing import List, Optional, Union
from datetime import datetime
from bson import ObjectId

from app.schemas.collection import (
    CollectionCreate,
    CollectionUpdate,
    CollectionResponse,
    CollectionWithMemories,
    CollectionPrivacy
)
from app.models.user import UserInDB
from app.core.security import get_current_user
from app.db.mongodb import get_collection

router = APIRouter()

def safe_object_id(id_str: str) -> ObjectId:
    """Safely convert string to ObjectId, raise 400 if invalid"""
    try:
        return ObjectId(id_str)
    except Exception:
        raise HTTPException(status_code=400, detail="Invalid collection ID format")

async def _prepare_collection_response(col_doc: dict, current_user_id: str, include_memories: bool = False) -> Union[CollectionResponse, CollectionWithMemories]:
    """Prepare collection document for API response with error handling"""
    try:
        # Safely get owner information
        owner = await get_collection("users").find_one({"_id": col_doc.get("owner_id")})
        owner_name = "Unknown User"
        if owner:
            owner_name = owner.get("full_name") or owner.get("email", "Unknown User")

        # Count memories in collection
        memory_count = await get_collection("collection_memories").count_documents({
            "collection_id": col_doc["_id"]
        })

        base_data = {
            "id": str(col_doc["_id"]),
            "name": col_doc.get("name", "Untitled Collection"),
            "description": col_doc.get("description"),
            "cover_image_url": col_doc.get("cover_image_url"),
            "privacy": col_doc.get("privacy", CollectionPrivacy.PRIVATE),
            "tags": col_doc.get("tags", []),
            "owner_id": str(col_doc.get("owner_id", "")),
            "owner_name": owner_name,
            "memory_count": memory_count,
            "created_at": col_doc.get("created_at", datetime.utcnow()),
            "updated_at": col_doc.get("updated_at", datetime.utcnow()),
            "is_owner": str(col_doc.get("owner_id", "")) == current_user_id
        }

        if include_memories:
            try:
                memory_docs = await get_collection("collection_memories").find({
                    "collection_id": col_doc["_id"]
                }).to_list(length=None)

                base_data["memory_ids"] = [str(doc["memory_id"]) for doc in memory_docs if "memory_id" in doc]
                return CollectionWithMemories(**base_data)
            except Exception as e:
                # If memory fetching fails, return without memories
                base_data["memory_ids"] = []
                return CollectionWithMemories(**base_data)

        return CollectionResponse(**base_data)
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error preparing collection response: {str(e)}")

@router.post("/", response_model=CollectionResponse, status_code=status.HTTP_201_CREATED)
async def create_collection(
    collection: CollectionCreate,
    current_user: UserInDB = Depends(get_current_user)
):
    """Create a new collection"""
    try:
        collection_data = {
            "name": collection.name or "Untitled Collection",
            "description": collection.description,
            "cover_image_url": collection.cover_image_url,
            "privacy": collection.privacy or CollectionPrivacy.PRIVATE,
            "tags": collection.tags or [],
            "owner_id": ObjectId(current_user.id),
            "created_at": datetime.utcnow(),
            "updated_at": datetime.utcnow()
        }

        result = await get_collection("collections").insert_one(collection_data)
        col_doc = await get_collection("collections").find_one({"_id": result.inserted_id})

        if not col_doc:
            raise HTTPException(status_code=500, detail="Failed to create collection")

        return await _prepare_collection_response(col_doc, current_user.id)
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error creating collection: {str(e)}")

@router.get("/", response_model=List[CollectionResponse])
async def list_collections(
    privacy: Optional[CollectionPrivacy] = None,
    tag: Optional[str] = None,
    search: Optional[str] = None,
    page: int = Query(1, ge=1),
    limit: int = Query(20, ge=1, le=100),
    current_user: UserInDB = Depends(get_current_user)
):
    """List collections with filtering"""
    try:
        query = {"owner_id": ObjectId(current_user.id)}

        if privacy:
            query["privacy"] = privacy
        if tag:
            query["tags"] = tag
        if search:
            query["name"] = {"$regex": search, "$options": "i"}

        skip = (page - 1) * limit
        cursor = get_collection("collections").find(query).sort("updated_at", -1).skip(skip).limit(limit)

        collections = []
        async for col_doc in cursor:
            try:
                collections.append(await _prepare_collection_response(col_doc, current_user.id))
            except Exception:
                # Skip collections that fail to process
                continue

        return collections
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error listing collections: {str(e)}")

@router.get("/{collection_id}", response_model=CollectionWithMemories)
async def get_collection_detail(
    collection_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Get collection details with memories"""
    try:
        col_obj_id = safe_object_id(collection_id)
        col_doc = await get_collection("collections").find_one({"_id": col_obj_id})

        if not col_doc:
            raise HTTPException(status_code=404, detail="Collection not found")

        # Check access permissions
        is_owner = str(col_doc.get("owner_id")) == current_user.id
        collection_privacy = col_doc.get("privacy", CollectionPrivacy.PRIVATE)

        if not is_owner and collection_privacy == CollectionPrivacy.PRIVATE:
            raise HTTPException(status_code=403, detail="Not authorized to view this collection")

        return await _prepare_collection_response(col_doc, current_user.id, include_memories=True)
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error fetching collection: {str(e)}")

@router.put("/{collection_id}", response_model=CollectionResponse)
async def update_collection(
    collection_id: str,
    collection_update: CollectionUpdate,
    current_user: UserInDB = Depends(get_current_user)
):
    """Update a collection"""
    try:
        col_obj_id = safe_object_id(collection_id)
        col_doc = await get_collection("collections").find_one({"_id": col_obj_id})

        if not col_doc:
            raise HTTPException(status_code=404, detail="Collection not found")

        if str(col_doc.get("owner_id")) != current_user.id:
            raise HTTPException(status_code=403, detail="Not authorized to edit this collection")

        update_data = collection_update.dict(exclude_unset=True)
        update_data["updated_at"] = datetime.utcnow()

        await get_collection("collections").update_one(
            {"_id": col_obj_id},
            {"$set": update_data}
        )

        updated_doc = await get_collection("collections").find_one({"_id": col_obj_id})
        if not updated_doc:
            raise HTTPException(status_code=500, detail="Failed to update collection")

        return await _prepare_collection_response(updated_doc, current_user.id)
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error updating collection: {str(e)}")

@router.delete("/{collection_id}", status_code=status.HTTP_204_NO_CONTENT)
async def delete_collection(
    collection_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Delete a collection"""
    try:
        col_obj_id = safe_object_id(collection_id)
        col_doc = await get_collection("collections").find_one({"_id": col_obj_id})

        if not col_doc:
            raise HTTPException(status_code=404, detail="Collection not found")

        if str(col_doc.get("owner_id")) != current_user.id:
            raise HTTPException(status_code=403, detail="Not authorized to delete this collection")

        # Delete collection and all memory associations
        await get_collection("collections").delete_one({"_id": col_obj_id})
        await get_collection("collection_memories").delete_many({"collection_id": col_obj_id})

        # Revoke all share links for this collection
        await get_collection("share_links").update_many(
            {"resource_type": "collection", "resource_id": col_obj_id},
            {"$set": {"is_active": False}}
        )

        return None
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error deleting collection: {str(e)}")

@router.post("/{collection_id}/memories/{memory_id}", status_code=status.HTTP_200_OK)
async def add_memory_to_collection(
    collection_id: str,
    memory_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Add a memory to a collection"""
    try:
        col_obj_id = safe_object_id(collection_id)
        mem_obj_id = safe_object_id(memory_id)

        col_doc = await get_collection("collections").find_one({"_id": col_obj_id})
        if not col_doc:
            raise HTTPException(status_code=404, detail="Collection not found")

        if str(col_doc.get("owner_id")) != current_user.id:
            raise HTTPException(status_code=403, detail="Not authorized to modify this collection")

        memory_doc = await get_collection("memories").find_one({"_id": mem_obj_id})
        if not memory_doc:
            raise HTTPException(status_code=404, detail="Memory not found")

        # Check if memory is already in collection
        existing = await get_collection("collection_memories").find_one({
            "collection_id": col_obj_id,
            "memory_id": mem_obj_id
        })

        if existing:
            return {"message": "Memory already in collection"}

        await get_collection("collection_memories").insert_one({
            "collection_id": col_obj_id,
            "memory_id": mem_obj_id,
            "added_at": datetime.utcnow()
        })

        return {"message": "Memory added to collection successfully"}
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error adding memory: {str(e)}")

@router.delete("/{collection_id}/memories/{memory_id}", status_code=status.HTTP_200_OK)
async def remove_memory_from_collection(
    collection_id: str,
    memory_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Remove a memory from a collection"""
    try:
        col_obj_id = safe_object_id(collection_id)
        mem_obj_id = safe_object_id(memory_id)

        col_doc = await get_collection("collections").find_one({"_id": col_obj_id})
        if not col_doc:
            raise HTTPException(status_code=404, detail="Collection not found")

        if str(col_doc.get("owner_id")) != current_user.id:
            raise HTTPException(status_code=403, detail="Not authorized to modify this collection")

        result = await get_collection("collection_memories").delete_one({
            "collection_id": col_obj_id,
            "memory_id": mem_obj_id
        })

        if result.deleted_count == 0:
            return {"message": "Memory not in collection"}

        return {"message": "Memory removed from collection successfully"}
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error removing memory: {str(e)}")

@router.get("/{collection_id}/memories", response_model=List[dict])
async def get_collection_memories(
    collection_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Get all memories in a collection"""
    try:
        col_obj_id = safe_object_id(collection_id)
        col_doc = await get_collection("collections").find_one({"_id": col_obj_id})

        if not col_doc:
            raise HTTPException(status_code=404, detail="Collection not found")

        # Check access permissions
        is_owner = str(col_doc.get("owner_id")) == current_user.id
        collection_privacy = col_doc.get("privacy", CollectionPrivacy.PRIVATE)

        if not is_owner and collection_privacy == CollectionPrivacy.PRIVATE:
            raise HTTPException(status_code=403, detail="Not authorized to view this collection")

        memory_links = await get_collection("collection_memories").find({
            "collection_id": col_obj_id
        }).to_list(length=None)

        memories = []
        for link in memory_links:
            try:
                memory_doc = await get_collection("memories").find_one({"_id": link.get("memory_id")})
                if memory_doc:
                    owner = await get_collection("users").find_one({"_id": memory_doc.get("owner_id")})

                    memories.append({
                        "id": str(memory_doc["_id"]),
                        "title": memory_doc.get("title", "Untitled"),
                        "content": memory_doc.get("content", ""),
                        "image_url": memory_doc.get("media_urls", [None])[0] if memory_doc.get("media_urls") else None,
                        "owner_name": owner.get("full_name", "Unknown") if owner else "Unknown",
                        "created_at": memory_doc.get("created_at", datetime.utcnow()).isoformat(),
                        "privacy": memory_doc.get("privacy", "private"),
                        "tags": memory_doc.get("tags", [])
                    })
            except Exception:
                # Skip memories that fail to process
                continue

        return memories
    except HTTPException:
        raise
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error fetching collection memories: {str(e)}")

