from fastapi import APIRouter, Depends, HTTPException, status, Query
from typing import List, Optional
from datetime import datetime
from bson import ObjectId

from app.schemas.notification import (
    NotificationResponse,
    NotificationListResponse,
    NotificationType
)
from app.models.user import UserInDB
from app.core.security import get_current_user
from app.db.mongodb import get_collection

router = APIRouter()

async def _prepare_notification_response(notif_doc: dict) -> NotificationResponse:
    """Prepare notification document for API response"""
    actor = await get_collection("users").find_one({"_id": notif_doc["actor_id"]})

    return NotificationResponse(
        id=str(notif_doc["_id"]),
        type=notif_doc["type"],
        title=notif_doc["title"],
        message=notif_doc["message"],
        target_type=notif_doc.get("target_type"),
        target_id=str(notif_doc["target_id"]) if notif_doc.get("target_id") else None,
        actor_id=str(notif_doc["actor_id"]),
        actor_name=actor.get("full_name") if actor else "Unknown User",
        actor_avatar=actor.get("avatar_url") if actor else None,
        is_read=notif_doc.get("is_read", False),
        created_at=notif_doc["created_at"]
    )

async def create_notification(
    user_id: str,
    notification_type: NotificationType,
    title: str,
    message: str,
    actor_id: str,
    target_type: Optional[str] = None,
    target_id: Optional[str] = None
):
    """Helper function to create a notification"""
    notification_data = {
        "user_id": ObjectId(user_id),
        "type": notification_type,
        "title": title,
        "message": message,
        "actor_id": ObjectId(actor_id),
        "is_read": False,
        "created_at": datetime.utcnow()
    }

    if target_type:
        notification_data["target_type"] = target_type
    if target_id:
        notification_data["target_id"] = ObjectId(target_id)

    await get_collection("notifications").insert_one(notification_data)

@router.get("/", response_model=NotificationListResponse)
async def list_notifications(
    is_read: Optional[bool] = None,
    notification_type: Optional[NotificationType] = None,
    page: int = Query(1, ge=1),
    limit: int = Query(20, ge=1, le=100),
    current_user: UserInDB = Depends(get_current_user)
):
    """List notifications for current user"""
    query = {"user_id": ObjectId(current_user.id)}

    if is_read is not None:
        query["is_read"] = is_read
    if notification_type:
        query["type"] = notification_type

    total = await get_collection("notifications").count_documents(query)
    unread_count = await get_collection("notifications").count_documents({
        "user_id": ObjectId(current_user.id),
        "is_read": False
    })

    skip = (page - 1) * limit
    pages = (total + limit - 1) // limit

    cursor = get_collection("notifications").find(query).sort("created_at", -1).skip(skip).limit(limit)

    notifications = []
    async for notif_doc in cursor:
        notifications.append(await _prepare_notification_response(notif_doc))

    return NotificationListResponse(
        notifications=notifications,
        total=total,
        unread_count=unread_count,
        page=page,
        pages=pages
    )

@router.put("/{notification_id}/read", status_code=status.HTTP_200_OK)
async def mark_as_read(
    notification_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Mark a notification as read"""
    notif = await get_collection("notifications").find_one({
        "_id": ObjectId(notification_id),
        "user_id": ObjectId(current_user.id)
    })

    if not notif:
        raise HTTPException(status_code=404, detail="Notification not found")

    await get_collection("notifications").update_one(
        {"_id": ObjectId(notification_id)},
        {"$set": {"is_read": True}}
    )

    return {"message": "Notification marked as read"}

@router.put("/read-all", status_code=status.HTTP_200_OK)
async def mark_all_as_read(
    current_user: UserInDB = Depends(get_current_user)
):
    """Mark all notifications as read"""
    result = await get_collection("notifications").update_many(
        {"user_id": ObjectId(current_user.id), "is_read": False},
        {"$set": {"is_read": True}}
    )

    return {"message": f"{result.modified_count} notifications marked as read"}

@router.delete("/{notification_id}", status_code=status.HTTP_204_NO_CONTENT)
async def delete_notification(
    notification_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Delete a notification"""
    result = await get_collection("notifications").delete_one({
        "_id": ObjectId(notification_id),
        "user_id": ObjectId(current_user.id)
    })

    if result.deleted_count == 0:
        raise HTTPException(status_code=404, detail="Notification not found")

@router.delete("/", status_code=status.HTTP_200_OK)
async def delete_all_notifications(
    current_user: UserInDB = Depends(get_current_user)
):
    """Delete all notifications for current user"""
    result = await get_collection("notifications").delete_many({
        "user_id": ObjectId(current_user.id)
    })

    return {"message": f"{result.deleted_count} notifications deleted"}

app/api/v1/endpoints/admin/export.py.content:
from fastapi import APIRouter, Depends, HTTPException, BackgroundTasks
from fastapi.responses import FileResponse
from typing import Optional
from datetime import datetime
from bson import ObjectId
import os
import zipfile
import json

from app.models.user import UserInDB
from app.core.security import get_current_user
from app.db.mongodb import get_collection

router = APIRouter()

EXPORT_DIR = "exports"
os.makedirs(EXPORT_DIR, exist_ok=True)

@router.post("/memories/json")
async def export_memories_json(
    start_date: Optional[str] = None,
    end_date: Optional[str] = None,
    current_user: UserInDB = Depends(get_current_user)
):
    """Export memories as JSON"""
    query = {"owner_id": ObjectId(current_user.id)}

    if start_date:
        query["created_at"] = query.get("created_at", {})
        query["created_at"]["$gte"] = datetime.fromisoformat(start_date)

    if end_date:
        query["created_at"] = query.get("created_at", {})
        query["created_at"]["$lte"] = datetime.fromisoformat(end_date)

    memories = await get_collection("memories").find(query).to_list(length=None)

    # Convert ObjectId to string for JSON serialization
    for memory in memories:
        memory["_id"] = str(memory["_id"])
        memory["owner_id"] = str(memory["owner_id"])
        if "created_at" in memory:
            memory["created_at"] = memory["created_at"].isoformat()
        if "updated_at" in memory:
            memory["updated_at"] = memory["updated_at"].isoformat()

    # Save to file
    filename = f"memories_export_{current_user.id}_{datetime.utcnow().strftime('%Y%m%d_%H%M%S')}.json"
    filepath = os.path.join(EXPORT_DIR, filename)

    with open(filepath, "w") as f:
        json.dump({"memories": memories, "exported_at": datetime.utcnow().isoformat()}, f, indent=2)

    return {
        "download_url": f"/api/v1/export/download/{filename}",
        "filename": filename,
        "count": len(memories)
    }

@router.post("/files/zip")
async def export_files_zip(
    file_ids: Optional[list[str]] = None,
    current_user: UserInDB = Depends(get_current_user)
):
    """Export files as ZIP archive"""
    query = {"owner_id": ObjectId(current_user.id)}

    if file_ids:
        query["_id"] = {"$in": [ObjectId(fid) for fid in file_ids]}

    files = await get_collection("files").find(query).to_list(length=None)

    if not files:
        raise HTTPException(status_code=404, detail="No files found to export")

    # Create ZIP file
    zip_filename = f"files_export_{current_user.id}_{datetime.utcnow().strftime('%Y%m%d_%H%M%S')}.zip"
    zip_filepath = os.path.join(EXPORT_DIR, zip_filename)

    with zipfile.ZipFile(zip_filepath, 'w', zipfile.ZIP_DEFLATED) as zipf:
        for file_doc in files:
            file_path = file_doc.get("file_path")
            if file_path and os.path.exists(file_path):
                # Add file to ZIP with its original name
                arcname = file_doc.get("name", os.path.basename(file_path))
                zipf.write(file_path, arcname=arcname)

    return {
        "download_url": f"/api/v1/export/download/{zip_filename}",
        "filename": zip_filename,
        "files_count": len(files)
    }

@router.post("/full-backup")
async def create_full_backup(
    current_user: UserInDB = Depends(get_current_user)
):
    """Create a full backup of all user data"""
    # Export all data
    memories = await get_collection("memories").find({"owner_id": ObjectId(current_user.id)}).to_list(length=None)
    files = await get_collection("files").find({"owner_id": ObjectId(current_user.id)}).to_list(length=None)
    hub_items = await get_collection("hub_items").find({"owner_id": ObjectId(current_user.id)}).to_list(length=None)
    collections = await get_collection("collections").find({"owner_id": ObjectId(current_user.id)}).to_list(length=None)

    # Convert ObjectIds to strings
    def convert_doc(doc):
        doc["_id"] = str(doc["_id"])
        doc["owner_id"] = str(doc["owner_id"])
        if "created_at" in doc:
            doc["created_at"] = doc["created_at"].isoformat()
        if "updated_at" in doc:
            doc["updated_at"] = doc["updated_at"].isoformat()
        return doc

    backup_data = {
        "user_id": current_user.id,
        "backup_date": datetime.utcnow().isoformat(),
        "memories": [convert_doc(m) for m in memories],
        "files": [convert_doc(f) for f in files],
        "hub_items": [convert_doc(h) for h in hub_items],
        "collections": [convert_doc(c) for c in collections]
    }

    # Save backup
    filename = f"full_backup_{current_user.id}_{datetime.utcnow().strftime('%Y%m%d_%H%M%S')}.json"
    filepath = os.path.join(EXPORT_DIR, filename)

    with open(filepath, "w") as f:
        json.dump(backup_data, f, indent=2)

    return {
        "download_url": f"/api/v1/export/download/{filename}",
        "filename": filename,
        "stats": {
            "memories": len(memories),
            "files": len(files),
            "hub_items": len(hub_items),
            "collections": len(collections)
        }
    }

@router.get("/download/{filename}")
async def download_export(
    filename: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Download an exported file"""
    # Verify filename belongs to current user
    if current_user.id not in filename:
        raise HTTPException(status_code=403, detail="Not authorized to download this file")

    filepath = os.path.join(EXPORT_DIR, filename)

    if not os.path.exists(filepath):
        raise HTTPException(status_code=404, detail="Export file not found")

    return FileResponse(
        path=filepath,
        filename=filename,
        media_type="application/octet-stream"
    )

# Alias endpoints for better API compatibility
@router.post("/json")
async def export_json_alias(
    start_date: Optional[str] = None,
    end_date: Optional[str] = None,
    current_user: UserInDB = Depends(get_current_user)
):
    """Alias for /memories/json endpoint"""
    return await export_memories_json(start_date, end_date, current_user)

@router.post("/archive")
async def export_archive_alias(
    file_ids: Optional[list[str]] = None,
    current_user: UserInDB = Depends(get_current_user)
):
    """Alias for /files/zip endpoint"""
    return await export_files_zip(file_ids, current_user)

@router.get("/history")
async def export_history(
    current_user: UserInDB = Depends(get_current_user)
):
    """Get export history for current user"""
    # Return list of exports created by user
    files = []
    if os.path.exists(EXPORT_DIR):
        for filename in os.listdir(EXPORT_DIR):
            if current_user.id in filename:
                filepath = os.path.join(EXPORT_DIR, filename)
                stat = os.stat(filepath)
                files.append({
                    "filename": filename,
                    "download_url": f"/api/v1/export/download/{filename}",
                    "size": stat.st_size,
                    "created_at": datetime.fromtimestamp(stat.st_ctime).isoformat()
                })

    return sorted(files, key=lambda x: x['created_at'], reverse=True)

