from fastapi import APIRouter, Depends, Query
from typing import List, Dict, Any
from bson import ObjectId

from app.models.user import UserInDB
from app.core.security import get_current_user
from app.db.mongodb import get_collection

router = APIRouter()

@router.get("/")
async def list_tags(
    sort_by: str = Query("count", regex="^(count|name)$"),
    current_user: UserInDB = Depends(get_current_user)
):
    """List all tags used by the user with counts"""
    # Aggregate tags from memories
    memory_tags = await get_collection("memories").aggregate([
        {"$match": {"owner_id": ObjectId(current_user.id)}},
        {"$unwind": "$tags"},
        {"$group": {"_id": "$tags", "count": {"$sum": 1}}},
        {"$project": {"tag": "$_id", "count": 1, "_id": 0}}
    ]).to_list(length=None)

    # Aggregate tags from files
    file_tags = await get_collection("files").aggregate([
        {"$match": {"owner_id": ObjectId(current_user.id)}},
        {"$unwind": "$tags"},
        {"$group": {"_id": "$tags", "count": {"$sum": 1}}},
        {"$project": {"tag": "$_id", "count": 1, "_id": 0}}
    ]).to_list(length=None)

    # Aggregate tags from hub items
    hub_tags = await get_collection("hub_items").aggregate([
        {"$match": {"owner_id": ObjectId(current_user.id)}},
        {"$unwind": "$tags"},
        {"$group": {"_id": "$tags", "count": {"$sum": 1}}},
        {"$project": {"tag": "$_id", "count": 1, "_id": 0}}
    ]).to_list(length=None)

    # Merge all tags
    tag_map: Dict[str, int] = {}
    for tag_data in memory_tags + file_tags + hub_tags:
        tag = tag_data["tag"]
        count = tag_data["count"]
        tag_map[tag] = tag_map.get(tag, 0) + count

    tags = [{"tag": tag, "count": count} for tag, count in tag_map.items()]

    # Sort
    if sort_by == "count":
        tags.sort(key=lambda x: x["count"], reverse=True)
    else:
        tags.sort(key=lambda x: x["tag"])

    return {"tags": tags}

@router.get("/popular")
async def get_popular_tags(
    limit: int = Query(10, ge=1, le=50),
    current_user: UserInDB = Depends(get_current_user)
):
    """Get most popular tags"""
    tags = await list_tags("count", current_user)
    return {"tags": tags["tags"][:limit]}

@router.get("/{tag}/content")
async def get_content_by_tag(
    tag: str,
    content_type: str = Query(None, regex="^(memory|file|hub_item|collection)$"),
    page: int = Query(1, ge=1),
    limit: int = Query(20, ge=1, le=100),
    current_user: UserInDB = Depends(get_current_user)
):
    """Get all content with a specific tag"""
    results = []

    # Get memories with tag
    if not content_type or content_type == "memory":
        memories = await get_collection("memories").find({
            "owner_id": ObjectId(current_user.id),
            "tags": tag
        }).limit(limit).to_list(length=None)

        for memory in memories:
            results.append({
                "type": "memory",
                "id": str(memory["_id"]),
                "title": memory["title"],
                "created_at": memory.get("created_at")
            })

    # Get files with tag
    if not content_type or content_type == "file":
        files = await get_collection("files").find({
            "owner_id": ObjectId(current_user.id),
            "tags": tag
        }).limit(limit).to_list(length=None)

        for file in files:
            results.append({
                "type": "file",
                "id": str(file["_id"]),
                "name": file["name"],
                "created_at": file.get("created_at")
            })

    # Get hub items with tag
    if not content_type or content_type == "hub_item":
        hub_items = await get_collection("hub_items").find({
            "owner_id": ObjectId(current_user.id),
            "tags": tag
        }).limit(limit).to_list(length=None)

        for item in hub_items:
            results.append({
                "type": "hub_item",
                "id": str(item["_id"]),
                "title": item["title"],
                "created_at": item.get("created_at")
            })

    # Get collections with tag
    if not content_type or content_type == "collection":
        collections = await get_collection("collections").find({
            "owner_id": ObjectId(current_user.id),
            "tags": tag
        }).limit(limit).to_list(length=None)

        for col in collections:
            results.append({
                "type": "collection",
                "id": str(col["_id"]),
                "name": col["name"],
                "created_at": col.get("created_at")
            })

    # Paginate
    skip = (page - 1) * limit
    paginated_results = results[skip:skip + limit]

    return {
        "tag": tag,
        "results": paginated_results,
        "total": len(results),
        "page": page
    }

@router.put("/{tag}/rename")
async def rename_tag(
    tag: str,
    new_tag: str = Query(..., min_length=1, max_length=50),
    current_user: UserInDB = Depends(get_current_user)
):
    """Rename a tag across all content"""
    # Update memories
    await get_collection("memories").update_many(
        {"owner_id": ObjectId(current_user.id), "tags": tag},
        {"$set": {"tags.$": new_tag}}
    )

    # Update files
    await get_collection("files").update_many(
        {"owner_id": ObjectId(current_user.id), "tags": tag},
        {"$set": {"tags.$": new_tag}}
    )

    # Update hub items
    await get_collection("hub_items").update_many(
        {"owner_id": ObjectId(current_user.id), "tags": tag},
        {"$set": {"tags.$": new_tag}}
    )

    # Update collections
    await get_collection("collections").update_many(
        {"owner_id": ObjectId(current_user.id), "tags": tag},
        {"$set": {"tags.$": new_tag}}
    )

    return {"message": f"Tag '{tag}' renamed to '{new_tag}'"}

@router.delete("/{tag}")
async def delete_tag(
    tag: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Delete a tag from all content"""
    # Remove from memories
    await get_collection("memories").update_many(
        {"owner_id": ObjectId(current_user.id)},
        {"$pull": {"tags": tag}}
    )

    # Remove from files
    await get_collection("files").update_many(
        {"owner_id": ObjectId(current_user.id)},
        {"$pull": {"tags": tag}}
    )

    # Remove from hub items
    await get_collection("hub_items").update_many(
        {"owner_id": ObjectId(current_user.id)},
        {"$pull": {"tags": tag}}
    )

    # Remove from collections
    await get_collection("collections").update_many(
        {"owner_id": ObjectId(current_user.id)},
        {"$pull": {"tags": tag}}
    )

    return {"message": f"Tag '{tag}' deleted from all content"}

app/api/v1/endpoints/notifications.py.content:
from fastapi import APIRouter, Depends, HTTPException, status, Query
from typing import List, Optional
from datetime import datetime
from bson import ObjectId

from app.schemas.notification import (
    NotificationResponse,
    NotificationListResponse,
    NotificationType
)
from app.models.user import UserInDB
from app.core.security import get_current_user
from app.db.mongodb import get_collection

router = APIRouter()

async def _prepare_notification_response(notif_doc: dict) -> NotificationResponse:
    """Prepare notification document for API response"""
    actor = await get_collection("users").find_one({"_id": notif_doc["actor_id"]})

    return NotificationResponse(
        id=str(notif_doc["_id"]),
        type=notif_doc["type"],
        title=notif_doc["title"],
        message=notif_doc["message"],
        target_type=notif_doc.get("target_type"),
        target_id=str(notif_doc["target_id"]) if notif_doc.get("target_id") else None,
        actor_id=str(notif_doc["actor_id"]),
        actor_name=actor.get("full_name") if actor else "Unknown User",
        actor_avatar=actor.get("avatar_url") if actor else None,
        is_read=notif_doc.get("is_read", False),
        created_at=notif_doc["created_at"]
    )

async def create_notification(
    user_id: str,
    notification_type: NotificationType,
    title: str,
    message: str,
    actor_id: str,
    target_type: Optional[str] = None,
    target_id: Optional[str] = None
):
    """Helper function to create a notification"""
    notification_data = {
        "user_id": ObjectId(user_id),
        "type": notification_type,
        "title": title,
        "message": message,
        "actor_id": ObjectId(actor_id),
        "is_read": False,
        "created_at": datetime.utcnow()
    }

    if target_type:
        notification_data["target_type"] = target_type
    if target_id:
        notification_data["target_id"] = ObjectId(target_id)

    await get_collection("notifications").insert_one(notification_data)

@router.get("/", response_model=NotificationListResponse)
async def list_notifications(
    is_read: Optional[bool] = None,
    notification_type: Optional[NotificationType] = None,
    page: int = Query(1, ge=1),
    limit: int = Query(20, ge=1, le=100),
    current_user: UserInDB = Depends(get_current_user)
):
    """List notifications for current user"""
    query = {"user_id": ObjectId(current_user.id)}

    if is_read is not None:
        query["is_read"] = is_read
    if notification_type:
        query["type"] = notification_type

    total = await get_collection("notifications").count_documents(query)
    unread_count = await get_collection("notifications").count_documents({
        "user_id": ObjectId(current_user.id),
        "is_read": False
    })

    skip = (page - 1) * limit
    pages = (total + limit - 1) // limit

    cursor = get_collection("notifications").find(query).sort("created_at", -1).skip(skip).limit(limit)

    notifications = []
    async for notif_doc in cursor:
        notifications.append(await _prepare_notification_response(notif_doc))

    return NotificationListResponse(
        notifications=notifications,
        total=total,
        unread_count=unread_count,
        page=page,
        pages=pages
    )

@router.put("/{notification_id}/read", status_code=status.HTTP_200_OK)
async def mark_as_read(
    notification_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Mark a notification as read"""
    notif = await get_collection("notifications").find_one({
        "_id": ObjectId(notification_id),
        "user_id": ObjectId(current_user.id)
    })

    if not notif:
        raise HTTPException(status_code=404, detail="Notification not found")

    await get_collection("notifications").update_one(
        {"_id": ObjectId(notification_id)},
        {"$set": {"is_read": True}}
    )

    return {"message": "Notification marked as read"}

@router.put("/read-all", status_code=status.HTTP_200_OK)
async def mark_all_as_read(
    current_user: UserInDB = Depends(get_current_user)
):
    """Mark all notifications as read"""
    result = await get_collection("notifications").update_many(
        {"user_id": ObjectId(current_user.id), "is_read": False},
        {"$set": {"is_read": True}}
    )

    return {"message": f"{result.modified_count} notifications marked as read"}

@router.delete("/{notification_id}", status_code=status.HTTP_204_NO_CONTENT)
async def delete_notification(
    notification_id: str,
    current_user: UserInDB = Depends(get_current_user)
):
    """Delete a notification"""
    result = await get_collection("notifications").delete_one({
        "_id": ObjectId(notification_id),
        "user_id": ObjectId(current_user.id)
    })

    if result.deleted_count == 0:
        raise HTTPException(status_code=404, detail="Notification not found")

@router.delete("/", status_code=status.HTTP_200_OK)
async def delete_all_notifications(
    current_user: UserInDB = Depends(get_current_user)
):
    """Delete all notifications for current user"""
    result = await get_collection("notifications").delete_many({
        "user_id": ObjectId(current_user.id)
    })

    return {"message": f"{result.deleted_count} notifications deleted"}

